require 'selenium-webdriver'require 'webdrivers/chromedriver'driver = Selenium::WebDriver.for :chromedriver.navigate.to "http://automationpractice.com/index.php"#       SIgn In.#           Click on Sign Indriver.find_element(css: '#header > div.nav > div > div > nav > div.header_user_info > a').click#       Enter user and passdriver.find_element(id: 'email').send_keys "delvis1@delvis1.com"driver.find_element(id: 'passwd').send_keys "delvis1"#       Click on the buttondriver.find_element(id: 'SubmitLogin').click#       Select T-Shirtsdriver.find_element(css: '#block_top_menu > ul > li:nth-child(3) > a').click#       Select Quick Viewdriver.find_element(class: 'quick-view').clickdriver.quit